mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.sex_types as sexTypes ,yonghu.new_money as newMoney
FROM etc a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.yonghu_id = #{params.yonghuId}
and a.etc_number like CONCAT('%',#{params.etcNumber},'%')
and a.etc_money >= #{params.etcMoneyStart}
and a.etc_money <= #{params.etcMoneyEnd}
and a.etc_address like CONCAT('%',#{params.etcAddress},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
order by a.${params.orderBy} desc
